import { SignOut } from "phosphor-react";
import { Button } from "../Button/button";
import { useEffect, useState } from "react";
import AuthService from "@/services/auth.service";
import { Usuario } from "@/models/usuario";
import UsuarioService from "@/services/usuario.service";

// export interface ProfileProps {}

export function Profile() {
  const [dadosDoUsuario, setDadosDoUsuario] = useState("");
  
  useEffect(() => {
    const usuario = Usuario.fromJson(JSON.parse(localStorage.getItem("usuario")!))
    UsuarioService.findById(usuario.id).then(
      (response) => {
        console.log(response.data);
        setDadosDoUsuario(response.data.user);
      },
      (error) => {
        const _content =
          (error.response && error.response.data) 
          error.message 
          error.toString();
          setDadosDoUsuario(_content);
      }
    );
  }, []);

  return (
    <div className="flex items-center gap-3">
      <img
        src={dadosDoUsuario.imagem ? dadosDoUsuario.imagem : 'data:image/png;base64,'}
        className="h-10 w-10 rounded-full"
        alt=""
      />
      <div className="flex flex-col">
        <span className="block text-sm font-semibold text-zinc-700 dark:text-zinc-100">
          {dadosDoUsuario ? dadosDoUsuario.nome : 'Carregando...'}
        </span>
        <span className="block text-sm text-zinc-500 dark:text-zinc-400">
          {dadosDoUsuario ? dadosDoUsuario.email : ''}
        </span>
      </div>
      <Button variant="ghost" className="ml-auto">
        <SignOut className="h-5 w-5 textA-zinc-500 dark:text-zinc-400" />
      </Button>
    </div>
  );
}
